fn get_final_floor(instructions : &str) -> i32 {
    let mut floors = 0;
    for c in instructions.chars() {
        match c {         
            '(' => floors +=1,
            ')' => floors -=1,
            _  => floors += 0
        }
    }
    floors
}

fn get_position_of_entering_basement(instructions: &str) -> i32 {
    let mut floor = 0;
    let mut idx = 1;
    for c in instructions.chars() {
        match c {         
            '(' => floor +=1,
            ')' => floor -=1,
            _  => floor += 0
        }
        if floor == -1 {
            return idx;
        }
        idx +=1;
    }
    idx
}

